                    var reader = new FileReader();
                    reader.onload = function (e) {
                        $('#imgCapa').attr('src', e.target.result);
                    }
                    reader.readAsDataURL(input.files[0]);
                }
                else {
                    $('#imgCapa').attr('src', '~/images/capas/capa-vazia.png');
                }
            });
        });

        $(""#mybutton"").click(function () {
